set -eu

repo_root="$(readlink -f $(dirname ${0})/..)"
version=$(cat "${repo_root}/VERSION")

cat << EOF > "${repo_root}/example/shoot-cert-service/example/extension-patch.yaml"
# DO NOT EDIT THIS FILE!
# This file is auto-generated by hack/prepare-operator-extension.sh.

apiVersion: operator.gardener.cloud/v1alpha1
kind: Extension
metadata:
  name: extension-shoot-cert-service
spec:
  deployment:
    extension:
      helm:
        ociRepository:
          ref: europe-docker.pkg.dev/gardener-project/releases/charts/gardener/extensions/shoot-cert-service:$version
EOF

kubectl kustomize "${repo_root}/example/shoot-cert-service/example" -o "${repo_root}/example/extension-shoot-cert-service.yaml"
